import React, { useState } from "react";

const AddMeals = () => {
  const [mealName, setMealName] = useState("");
  const [category, setCategory] = useState("");
  const [instructions, setInstructions] = useState("");
  // Add more state variables for other meal data if needed

  const handleMealNameChange = (event) => {
    setMealName(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
  };

  const handleInstructionsChange = (event) => {
    setInstructions(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Create the new meal data object
    const newMealData = {
      strMeal: mealName,
      strCategory: category,
      strInstructions: instructions,
      // Add other properties for the new meal data if needed
    };

    // Make the POST request to add the new meal
    fetch("http://localhost:3000/meals", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newMealData),
    })
      .then((response) => response.json())
      .then((responseData) => {
        console.log("New meal added successfully:", responseData);
        // Clear the form fields after successful submission
        setMealName("");
        setCategory("");
        setInstructions("");
      })
      .catch((error) => console.error("Error adding new meal: ", error));
  };

  return (
    <div>
      <h1>Add New Meal</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="mealName">Meal Name:</label>
          <input
            type="text"
            id="mealName"
            value={mealName}
            onChange={handleMealNameChange}
            required
          />
        </div>
        <div>
          <label htmlFor="category">Category:</label>
          <input
            type="text"
            id="category"
            value={category}
            onChange={handleCategoryChange}
            required
          />
        </div>
        <div>
          <label htmlFor="instructions">Instructions:</label>
          <textarea
            id="instructions"
            value={instructions}
            onChange={handleInstructionsChange}
            required
          />
        </div>
        <button type="submit">Add Meal</button>
      </form>
    </div>
  );
};

export default AddMeals;
