import React, { useState, useEffect } from "react";

const Home = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    fetch("https://www.themealdb.com/api/json/v1/1/filter.php?c=Seafood")
      .then((response) => response.json())
      .then((responseData) => {
        if (Array.isArray(responseData.meals)) {
          setData(responseData.meals);
          // Fetch images for each meal one by one using Promise chain
          return Promise.all(
            responseData.meals.map((meal) =>
              fetch(
                `https://www.themealdb.com/api/json/v1/1/lookup.php?i=${meal.idMeal}`
              ).then((response) => response.json())
            )
          );
        } else {
          setData([]);
        }
      })
      .then((mealImageResponses) => {
        // Extract image URLs from the responses and update the state
        const mealImages = mealImageResponses.map(
          (mealImageResponse) => mealImageResponse.meals[0].strMealThumb
        );
        setData(mealImages);
      })
      .catch((error) => console.error("Error fetching data: ", error));
  };

  return (
    <div>
      <h1>Meal Page</h1>
      <ul>
        {data.map((image, index) => (
          <li key={index}>
            <img src={image} alt={`Meal ${index + 1}`} style={{ width: "100px" }} />
            {data[index].strMeal}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
